app = Flask(__name__)


def fetch_pdf_text(url):
    try:
        response = requests.get(url)
        response.raise_for_status()
        with tempfile.NamedTemporaryFile(delete=False, suffix=".pdf") as temp_file:
            temp_file.write(response.content)
            temp_filename = temp_file.name

        text = pdf_to_text(temp_filename)
        os.remove(temp_filename)

        return text

    except requests.exceptions.RequestException as e:
        logging.error(f"Failed to fetch PDF: {e}")
        return None


def pdf_to_text(pdf_path):
    try:
        laparams = LAParams(char_margin=2.0, line_margin=0.5)
        text = et(pdf_path, laparams=laparams)
        return text

    except Exception as e:
        logging.error(f"Error reading PDF: {e}")
        return None


@app.route('/extract_pdf_text', methods=['GET'])
def extract_pdf_text():
    pdf_url = request.args.get('q')
    if not pdf_url:
        logging.error("Missing PDF URL")
        return jsonify({"error": "Missing PDF URL"}), 400

    try:
        logging.info(f"Fetching PDF from URL: {pdf_url}")
        extracted_text = fetch_pdf_text(pdf_url)
        if extracted_text:
            return jsonify({"response": extracted_text})
        else:
            return jsonify({"error": "Failed to extract text from PDF"}), 500

    except Exception as e:
        logging.error(f"Error processing PDF: {e}")
        return jsonify({"error": f"Error processing PDF: {e}"}), 500


@app.route('/extract_text', methods=['GET'])
def extract_text():
    image_url = request.args.get('q')
    if not image_url:
        logging.error("Missing image URL")
        return jsonify({"error": "Missing image URL"}), 400

    try:
        logging.info(f"Fetching image from URL: {image_url}")
        response = requests.get(image_url)
        response.raise_for_status()

        image = Image.open(BytesIO(response.content))
        logging.info("Image successfully opened, performing OCR")
        text = pytesseract.image_to_string(image)

        logging.info("OCR successful")
        return jsonify({"response": text})
    except requests.exceptions.RequestException as e:
        logging.error(f"Error fetching image: {e}")
        return jsonify({"error": f"Error fetching image: {e}"}), 500
    except IOError as e:
        logging.error(f"Error opening image: {e}")
        return jsonify({"error": f"Error opening image: {e}"}), 500
    except Exception as e:
        logging.error(f"Unexpected error: {e}")
        return jsonify({"error": f"Unexpected error: {e}"}), 500


def clean_text(text):
    text = re.sub(r'[*]', '', text)
    text = re.sub(r'[^\w\s.,;:!?-]', '', text)
    return text


@app.route('/question_image', methods=['GET'])
def askImage():
    question = request.args.get('q', default='', type=str)
    context = request.args.get('context', default='', type=str)

    if not question:
        return jsonify({"error": "No question provided"}), 400

    chat_session.history.append({
        "role": "user",
        "parts": [context]
    })

    chat_session.history.append({
        "role": "user",
        "parts": [question]
    })

    response = chat_session.send_message(question)
    cleaned_text = clean_text(response.text)

    return jsonify({"response": cleaned_text})


@app.route('/ask', methods=['GET'])
def ask():
    question = request.args.get('q', default='', type=str)
    if not question:
        return jsonify({"error": "No question provided"}), 400

    chat_session.history.append({
        "role": "user",
        "parts": [question]
    })

    response = chat_session.send_message(question)
    cleaned_text = clean_text(response.text)

    return jsonify({"response": cleaned_text})


