def plot_anomaly_3d_with_colorbar(element, grid_resolution=200, colormap='viridis'):
    if element not in data.columns:
        return None, f"Element '{element}' not found in the data."

    lats = data['latitude'].values
    longs = data['longitude'].values
    values = data[element].values

    mean_value = np.mean(values)
    anomalies = np.maximum(values - mean_value, 0)

    grid_lat, grid_long = np.mgrid[lats.min():lats.max(
    ):grid_resolution*1j, longs.min():longs.max():grid_resolution*1j]
    grid_anomalies = griddata((lats, longs), anomalies,
                              (grid_lat, grid_long), method='cubic')
    grid_anomalies = np.nan_to_num(
        grid_anomalies, nan=0.0, posinf=0.0, neginf=0.0)

    fig = plt.figure(figsize=(10, 8))
    ax = fig.add_subplot(111, projection='3d')

    x, y = grid_lat.flatten(), grid_long.flatten()
    z = grid_anomalies.flatten()
    x = np.reshape(x, (grid_lat.shape[0], grid_long.shape[1]))
    y = np.reshape(y, (grid_lat.shape[0], grid_long.shape[1]))
    z = np.reshape(z, (grid_lat.shape[0], grid_long.shape[1]))

    surf = ax.plot_surface(x, y, z, cmap=colormap)
    cbar = fig.colorbar(surf, ax=ax, shrink=0.5, aspect=5)
    cbar.set_label(f'{element} anomaly (ppm)')
    ax.set_xlabel('Latitude')
    ax.set_ylabel('Longitude')
    ax.set_zlabel(f'{element} anomaly (ppm)')
    ax.set_title(f'3D Anomaly Plot for {element}')

    img_buf = BytesIO()
    plt.savefig(img_buf, format='png', dpi=300)
    img_buf.seek(0)
    img_base64 = base64.b64encode(img_buf.read()).decode('utf-8')

    plt.close()

    return img_base64, None


