def plot_3d_contour(element_name, grid_resolution=200, colormap='viridis'):
    if element_name not in df.columns:
        return None, f"Element '{element_name}' not found in the data."

    lon = df['longitude']
    lat = df['latitude']
    element = df[element_name]

    grid_x, grid_y = np.mgrid[lon.min():lon.max(
    ):grid_resolution*1j, lat.min():lat.max():grid_resolution*1j]
    grid_z = griddata((lon, lat), element, (grid_x, grid_y), method='cubic')

    fig = plt.figure()
    ax = fig.add_subplot(111, projection='3d')
    contour = ax.contour3D(grid_x, grid_y, grid_z, 100, cmap=colormap)

    mappable = plt.cm.ScalarMappable(cmap=colormap)
    mappable.set_array(element)
    plt.colorbar(mappable, ax=ax, shrink=0.5, aspect=5, label=element_name)

    ax.set_xlabel('Longitude')
    ax.set_ylabel('Latitude')
    ax.set_zlabel(element_name)
    ax.set_title(f'3D Contour Plot of {element_name}')

    img_buf = BytesIO()
    plt.savefig(img_buf, format='png', dpi=300)
    img_buf.seek(0)
    img_base64 = base64.b64encode(img_buf.read()).decode('utf-8')

    plt.close()

    return img_base64, None


