@app.route('/ask_data_question', methods=['GET'])
def ask2():
    question = request.args.get('q', default='', type=str)
    if not question:
        return jsonify({"error": "No question provided"}), 400

    chat_session_2.history.append({
        "role": "user",
        "parts": [question]
    })

    response = chat_session_2.send_message(question)
    cleaned_text = clean_text(response.text)

    return jsonify({"response": cleaned_text})


